        //         productPrice: "$40",
        //         productImage: null,
        //         productUrl: null,
        //         productTitle: "title1",
        //         productDescription: "description1"
        //     },
        //     {
        //         productId: "1",
        //         productName: 'item1',
        //         productPrice: "$40",
        //         productImage: null,
        //         productUrl: null,
        //         productTitle: "title1",
        //         productDescription: "description1"
        //     }
        // ]
    };




    render() {

        let products = this.state.data.products.map(product => {
            return (
                <Card product={product}/>
            );
        });


        return (
            <div>
                <div className={classes.sticky}>
                    <Navbar className={classes.nav} />
                </div>

                <div className={classes.container}>
                    <h1 className={classes.order}>Order</h1>

                    <div className="row">
                        <div className={`col-sm-3 ${classes.dropDownMenu}`}>
                            <Button btnType="dropdown-item">PICK-UP</Button>
                            <Button btnType="dropdown-item">DELIVERY</Button>
                            <Button btnType="dropdown">CURBSIDE PICK-UP</Button>
                        </div>

                        <div class="col-sm-9 d-flex flex-wrap justify-content-center">  
                            {products}
                        </div>
                    </div>
                </div>
            </div>
        )
    };
};

export default ProductList;
